<script>
export default {
  data() {
    return {
      seen:false,
      scrollPosition: null,
    }
  },

mounted() {
    window.addEventListener('scroll', this.updateScroll);

  },

   methods: {
      updateScroll() {
      this.scrollPosition = window.scrollY
      if (this.scrollPosition >= 500){
      this.seen=true;
      } else  {
      this.seen=false;
      }
    },
},

}
</script>

